class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Integer[] horizontal = Arrays.stream(horizontalCut).boxed().toArray(Integer[]::new);
        Integer[] vertical = Arrays.stream(verticalCut).boxed().toArray(Integer[]::new);

        Arrays.sort(horizontal, Collections.reverseOrder());
        Arrays.sort(vertical, Collections.reverseOrder());

        int hIndex = 0, vIndex = 0;
        long hPieces = 1, vPieces = 1;
        long totalCost = 0;

        while (hIndex < horizontal.length && vIndex < vertical.length) {
            if (horizontal[hIndex] >= vertical[vIndex]) {
                totalCost += horizontal[hIndex] * vPieces;
                hPieces++;
                hIndex++;
            } else {
                totalCost += vertical[vIndex] * hPieces;
                vPieces++;
                vIndex++;
            }
        }

        while (hIndex < horizontal.length) {
            totalCost += horizontal[hIndex] * vPieces;
            hPieces++;
            hIndex++;
        }

        while (vIndex < vertical.length) {
            totalCost += vertical[vIndex] * hPieces;
            vPieces++;
            vIndex++;
        }

        return totalCost;
    }
}
