class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        #define int long long 


        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        int i = 0, j = 0, hc = 0, vc = 0;
        int val = 0;
        while(i < m-1 && j < n-1){
            if(h[i] > v[j]){
                val += (h[i]*(vc+1));
                hc++;
                i++;
            }
            else{
                val += (v[j]*(hc+1));
                vc++;
                j++;
            }
        }

        while(i < m-1){
            val += (h[i]*(vc+1));
            hc++;
            i++;
        }

        while(j < n-1){
            val += (v[j]*(hc+1));
            vc++;
            j++;
        }

        return val;


        #undef int
    }
};
