class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, int>> arr;
        for (auto h : horizontalCut) {
            arr.push_back({h, 0});
        }
        for (auto v : verticalCut) {
            arr.push_back({v, 1});
        }
        sort(arr.begin(), arr.end(), greater());
        long long ans = 0;
        int x = 1;
        int y = 1;
        for (auto [v, d] : arr) {
            if (d) {
                ans += y * v;
                ++x;
            } else {
                ans += x * v;
                ++y;
            }
        }
        return ans;
    }
};
