class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, int> > p;
        for (int i = 0; i < m - 1; i++) {
            p.push_back({h[i], 0});
        }
        for (int i = 0; i < n - 1; i++) {
            p.push_back({v[i], 1});
        }
        sort(p.begin(), p.end());
        int hc = 0;
        int vc = 0;
        long long res = 0;
        for (int i = p.size() - 1; i >= 0; i--) {

            if (p[i].second) {
                res += 1LL * (hc + 1) * p[i].first;
                vc++;
            } else {
                res += 1LL * (vc + 1) * p[i].first;
                hc++;
            }
        }
        return res;
    }
};
