class Solution {
    public long minimumCost(int m, int n, int[] horti, int[] verti) {
        Arrays.sort(horti);
        Arrays.sort(verti);

        long h = 1;
        long v = 1;

        int i = horti.length-1;
        int j = verti.length-1;
        long ans = 0;
        while(i>=0 && j>=0){
            if(horti[i]>verti[j]){
                ans = ans + ((long)horti[i]*h);
                i--;
                v++;
            }else{
                ans = ans + ((long)verti[j]*v);
                j--;
                h++;
            }
        }

        while(i>=0){
            ans = ans + ((long)horti[i]*h);
            i--;
            v++;
        }

        while(j>=0){
            ans = ans + ((long)verti[j]*v);
            j--;
            h++;
        }
        return ans;
    }
}
