class Solution:
    def minimumCost(self, rows, cols, horz_cuts, vert_cuts):
        cut_pairs = [(0, 0)] * (cols + rows - 2)

        for i in range(rows - 1):
            cut_pairs[i] = (horz_cuts[i], 0)

        for i in range(cols - 1):
            cut_pairs[i + rows - 1] = (vert_cuts[i], 1)

        def sort_func(x):
            return x
        cut_pairs.sort(key=sort_func, reverse=True)

        cut_counts = [0, 0]
        min_cost = 0

        for i in range(cols + rows - 2):
            min_cost += cut_pairs[i][0] * (cut_counts[cut_pairs[i][1] ^ 1] + 1)
            cut_counts[cut_pairs[i][1]] += 1

        return min_cost
