class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans=0;
        vector<pair<long long,int>> cuts;
        for(int i=0;i<m-1;i++)
        {
            ans+=horizontalCut[i];
            cuts.push_back({horizontalCut[i],0});
        }
        for(int i=0;i<n-1;i++)
        {
            ans+=verticalCut[i];
            cuts.push_back({verticalCut[i],1});
        }
        sort(cuts.begin(),cuts.end());
        long long h=0,v=0;
        for(int i=cuts.size()-1;i>=0;i--)
        {
            if(cuts[i].second==0)
            {
                ans+=cuts[i].first*v;
                h++;
            }
            else
            {
                ans+=cuts[i].first*h;
                v++;
            }
        }
        return ans;
    }
};
