class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<ll, char>> pq;

        for (int i = 0; i < h.size(); i++) {
            pq.push({h[i], 'H'});
        }
        for (int j = 0; j < v.size(); j++) {
            pq.push({v[j], 'V'});
        }

        ll res = 0;
        int hsize = 1;
        int vsize = 1;

        while (!pq.empty()) {
            auto cut = pq.top();
            pq.pop();
            if (cut.second == 'H') {
                res += cut.first * vsize;
                hsize++;
            } else {
                res += cut.first * hsize;
                vsize++;
            }
        }

        return res;
    }
};
