class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        ans = 0
        cnt_h = 0 
        cnt_v = 0 
        while horizontalCut and verticalCut:
            if horizontalCut[-1] > verticalCut[-1]:
                ans += horizontalCut.pop()*(1+cnt_v)
                cnt_h += 1
            else:
                ans += verticalCut.pop()*(1+cnt_h)
                cnt_v += 1
        if horizontalCut:
            for c in horizontalCut:
                ans += c*(1+cnt_v)
        else:
            for c in verticalCut:
                ans += c*(1+cnt_h)
        return ans
