class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
         priority_queue<pair<int,int>>q;
            for(auto it : hor){
                q.push({it,1});
            }
            for(auto it : ver){
                q.push({it,0});
            }

           int h = 1,e = 1;

           long long ans = 0;

           while(!q.empty()){
                 auto it = q.top();
                 q.pop();

                  if(it.second) ans += it.first*e;
                  else ans += it.first*h;

                 if(it.second) h++;
                 else e++;
           }

        return ans;
    }
};
