class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut,
                          vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        long long totalCost = 0;
        int h = 1, v = 1; 
        int i = 0, j = 0; 

        while (i < m - 1 || j < n - 1) {
            if (j == n - 1 ||
                (i < m - 1 && horizontalCut[i] > verticalCut[j])) {
                totalCost += (long long)horizontalCut[i] * v;
                h++;
                i++;
            } else {
                totalCost += (long long)verticalCut[j] * h;
                v++;
                j++;
            }
        }

        return totalCost;
    }
};
