class Solution:
    def minimumCost(self, n, m, horizontalCut, verticalCut):
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)

        h, v = 1, 1  # Start with one piece of each dimension
        cost = 0
        i, j = 0, 0

        while i < len(horizontalCut) and j < len(verticalCut):
            if horizontalCut[i] > verticalCut[j]:
                cost += horizontalCut[i] * v
                h += 1
                i += 1
            else:
                cost += verticalCut[j] * h
                v += 1
                j += 1

        while i < len(horizontalCut):
            cost += horizontalCut[i] * v
            i += 1

        while j < len(verticalCut):
            cost += verticalCut[j] * h
            j += 1

        return cost
