struct node{
    int v;
    int id;
    bool operator<(node a){
        return v>a.v;
    } 
};
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<node>V(n+m-2);
        for(int i=0;i<m-1;i++) V[i].v=horizontalCut[i], V[i].id=1;
        for(int i=0;i<n-1;i++) V[m-1+i].v=verticalCut[i], V[m-1+i].id=2;
        sort(V.begin(),V.end());
        long long na=0,nb=0,ans=0;
        for(int i=0;i<n+m-2;i++){
            if(V[i].id==1){
                ans+=V[i].v*(nb+1);
                na++;
            }
            else{
                ans+=V[i].v*(na+1);
                nb++;
            }

        }
        return ans;
    }
};
