class Solution {
public:
  long long minimumCost(int m, int n, vector<int>& hCuts,vector<int>& vCuts) {
    sort(hCuts.begin(), hCuts.end(), greater<int>());
    sort(vCuts.begin(), vCuts.end(), greater<int>());
    int hIdx = 0, vIdx = 0;
    int hParts = 1, vParts = 1;
    long long totalCost = 0;
    while (hIdx < hCuts.size() && vIdx < vCuts.size()) {
        if (hCuts[hIdx] > vCuts[vIdx]) {
            totalCost += hCuts[hIdx] * vParts;
            hParts++;
            hIdx++;
        } else {
            totalCost += vCuts[vIdx] * hParts;
            vParts++;
            vIdx++;
        }
    }
    while (hIdx < hCuts.size()) {
        totalCost += hCuts[hIdx] * vParts;
        hParts++;
        hIdx++;
    }
    while (vIdx < vCuts.size()) {
        totalCost += vCuts[vIdx] * hParts;
        vParts++;
        vIdx++;
    }
    return totalCost;
}
};
