class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h = 1
        w = 1

        arr = []
        for hori in horizontalCut:
            arr.append((hori, 'hori'))
        for vert in verticalCut:
            arr.append((vert, 'vert'))
        arr.sort()

        ans = 0
        while h < m or w < n:
            cost, direction = arr.pop()
            if direction == 'vert':
                ans += cost * h
                w += 1
            elif direction == 'hori':
                ans += cost * w
                h += 1
        return ans
