class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        long long ans=0;
        int r=1,c=1;
        while(h.size() || v.size()){
            if(h.size() && v.size()){
                if(h.back()>v.back()){
                    ans+=r*h.back();
                    c++;
                    h.pop_back();
                }else{
                    ans+=c*v.back();
                    r++;
                    v.pop_back();
                }
            }else if(h.size()){
                ans+=r*h.back();
                c++;
                h.pop_back();
            }else{
                ans+=c*v.back();
                r++;
                v.pop_back();
            }
        }
        return ans;
    }
};
