class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         long long hcnt = 1, vcnt = 1;
        long long ans = 0;

        vector<pair<long long, long long>> p;
        for(int i=0;i<horizontalCut.size();i++){
            p.push_back({horizontalCut[i], 1});
        }

        for(auto j : verticalCut){
            p.push_back({j, 2});
        }

        sort(p.rbegin(), p.rend());

        for(auto i : p){

            long long val = i.first, t=i.second;

            if(t == 1){
                ans += 1LL * vcnt * val;
                hcnt++;
            }

            else{
                ans += 1LL * hcnt * val;
                vcnt++;
            }

        }

        return ans;

    }
};
