class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:

        horcut = deque(sorted(horizontalCut, reverse = True))
        vercut = deque(sorted(verticalCut, reverse = True))
        horp = 1
        verp = 1
        i, j = 0, 0
        ans = 0

        while horcut and vercut:
            if horcut[0] >= vercut[0]:
                ans += horcut.popleft()*verp
                horp += 1
            else:
                ans += vercut.popleft()*horp
                verp += 1

        while horcut:
            ans += horcut.popleft()*verp

        while vercut:
            ans += vercut.popleft()*horp

        return ans
