class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         vector<pair<long long,char>>vec;
        for(int i=0;i<h.size();i++){
            vec.push_back({h[i],'H'});
        }
        for(int i=0;i<v.size();i++){
            vec.push_back({v[i],'V'});
        }
        sort(vec.begin(),vec.end());
        reverse(vec.begin(),vec.end());
        long long hs=1;
        long long vs=1;
        long long tc=0;
        for(auto &it:vec){
            if(it.second=='H'){
                tc+=it.first*vs;
                hs++;
            }
            else{
                tc+=it.first*hs;
                vs++;
            }
        }
        return tc;
    }
};
