class Solution {
    public long minimumCost(int m, int n, int[] hor, int[] ver) {
        List<int[]> a=new ArrayList<>();
        for(int i=0;i<m-1;i++)
        {
            a.add(new int[]{hor[i],i,0});
        }
        for(int i=0;i<n-1;i++)
        {
            a.add(new int[]{ver[i],i,1});
        }
        Collections.sort(a,(x,y)->y[0]-x[0]);
        // for(int b[]:a)System.out.println(Arrays.toString(b));
        long ans=0;
        long h=1,v=1;
        for(int b[]:a){
            if(b[2]==1){
                v++;
                ans+=(h)*b[0];
            }
            else{
                h++;
                ans+=v*b[0];
            }
        }
        return ans;

    }
}
