#define ll long long
class Solution {
public:
  ll minimumCost(int n, int m, vector<int>& horizontalCut, vector<int>& verticalCut) {
    ll horizontalPrices = 1, verticalPrices = 1, result = 0;
    vector<pair<int, int>> tmp;
    for (int val : horizontalCut) tmp.push_back({val, 0});
    for (int val : verticalCut) tmp.push_back({val, 1});
    sort(tmp.rbegin(), tmp.rend());
    for (auto [val, type] : tmp) {
      if (type == 1) {
        result += (horizontalPrices * val);
        verticalPrices++;
      } else {
        result += (verticalPrices * val);
        horizontalPrices++;
      }
    }
    return result;
  }
};
