class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) { 
        vector<pair<int, char>> cuts;
        for (int cost : horizontalCut) {
            cuts.push_back({cost, 'H'});
        }
        for (int cost : verticalCut) {
            cuts.push_back({cost, 'V'});
        }
        sort(cuts.rbegin(), cuts.rend());

        long long totalCost = 0;
        int horizontalPieces = 1;
        int verticalPieces = 1;

        for (auto& cut : cuts) {
            int cost = cut.first;
            char cutType = cut.second;
            if (cutType == 'H') {
                totalCost += static_cast<long long>(cost) * verticalPieces;
                horizontalPieces += 1;
            } else {
                totalCost += static_cast<long long>(cost) * horizontalPieces;
                verticalPieces += 1;
            }
        }

        return totalCost;
    }
};
