class Solution:
    def minimumCost(self, m: int, n: int, hori_cut: List[int], vert_cut: List[int]) -> int:
        h_index, v_index = 0, 0
        h_pieces, v_pieces = 1, 1 
        res = 0
        hori_cut.sort(reverse=True)
        vert_cut.sort(reverse=True)

        while h_index < len(hori_cut) and v_index < len(vert_cut):
            if hori_cut[h_index] > vert_cut[v_index]:
                res += hori_cut[h_index] * v_pieces
                h_pieces += 1
                h_index += 1
            else:
                res += vert_cut[v_index] * h_pieces
                v_pieces += 1
                v_index += 1

        while h_index < len(hori_cut):
            res += hori_cut[h_index] * v_pieces
            h_index += 1

        while v_index < len(vert_cut):
            res += vert_cut[v_index] * h_pieces
            v_index += 1

        return res
