class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end());
        reverse(horizontalCut.begin(), horizontalCut.end());
        sort(verticalCut.begin(), verticalCut.end());
        reverse(verticalCut.begin(), verticalCut.end());

        int p1 = 0;
        int p2 = 0;
        long long ans = 0;
        while(p1!=m-1 || p2!=n-1){
            // cout << p1 << " "  << p2 << "\n";
            if(p1==m-1){
                ans += 1LL*verticalCut[p2]*1LL*(p1+1);
                p2++;
            }
            else if(p2==n-1){
                ans += 1LL*horizontalCut[p1]*1LL*(p2+1);
                p1++;
            }
            else{
                if(1LL*horizontalCut[p1]<1LL*verticalCut[p2]){
                    ans += 1LL*verticalCut[p2]*1LL*(p1+1);
                    p2++;
                }
                else if(1LL*horizontalCut[p1]>1LL*verticalCut[p2]){
                    ans += 1LL*horizontalCut[p1]*1LL*(p2+1);
                    p1++;
                }
                else{
                    if(p1<=p2){
                        ans += 1LL*horizontalCut[p1]*1LL*(p2+1);
                        p1++;
                    }
                    else{
                        ans += 1LL*verticalCut[p2]*1LL*(p1+1);
                        p2++;
                    }
                }
            }
        }
        return ans;
    }
};
