class Solution {
public:
    long long minimumCost(int rand1, int rand2, vector<int>& rand3, vector<int>& rand4) {
        sort(rand3.begin(), rand3.end(), greater<int>());
        sort(rand4.begin(), rand4.end(), greater<int>());

        int rand5 = 0, rand6 = 0;
        int rand7 = 1, rand8 = 1;
        long long rand9 = 0;

        while (rand5 < rand3.size() && rand6 < rand4.size()) {
            if (rand3[rand5] >= rand4[rand6]) {
                rand9 += rand3[rand5] * rand8;
                rand7++;
                rand5++;
            } else {
                rand9 += rand4[rand6] * rand7;
                rand8++;
                rand6++;
            }
        }

        while (rand5 < rand3.size()) {
            rand9 += rand3[rand5] * rand8;
            rand5++;
        }

        while (rand6 < rand4.size()) {
            rand9 += rand4[rand6] * rand7;
            rand6++;
        }

        return rand9;
    }
};
