class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> opt;
        for(int i = 0;i < m-1;i++){
            opt.push_back({horizontalCut[i],0});
        }
        for(int i = 0;i < n-1;i++){
            opt.push_back({verticalCut[i],1});
        }
        int h = 1,v = 1;
        long long ans = 0;
        sort(opt.rbegin(),opt.rend());
        for(auto i : opt){
            if(i.second){
                ans += 1ll*i.first*h;
                v++;
            }else{
                ans += 1ll*i.first*v;
                h++;
            }
        }
        return ans;
    }
};
