class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        int hi = 0, vi = 0;
        int hp = 1, vp = 1;
        long long total = 0;

        while (hi < hc.size() && vi < vc.size()) {
            if (hc[hi] > vc[vi]) {
                total += (long long)hc[hi] * vp;
                hp++;
                hi++;
            } else {
                total += (long long)vc[vi] * hp;
                vp++;
                vi++;
            }
        }

        while (hi < hc.size()) {
            total += (long long)hc[hi] * vp;
            hp++;
            hi++;
        }

        while (vi < vc.size()) {
            total += (long long)vc[vi] * hp;
            vp++;
            vi++;
        }

        return total;
    }
};
