class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& h, vector<int>& v) {
        long long ans=0;
         long i=n-2,j=m-2,a=1,b=1;
        sort(h.begin(),h.end());
        sort(v.begin(), v.end());
        while (i>=0 && j>=0) {
            if (h[i]>= v[j]) {
                ans+= (long long)h[i]*b;
                a++;
                i--;
            } else {
                ans += (long long)v[j] * a;
                j--;
                b++;
            }
        }
        while (j>=0) {
            ans+= (long long)v[j]*a;
            b++;
            j--;
        }
        while (i>=0) {
            ans += (long long)h[i] *b;
            a++;
            i--;
        }
        return ans;
    }
};
