class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        num_hori = num_vert = 1

        horizontalCut = sorted(horizontalCut)
        verticalCut = sorted(verticalCut)
        res = 0
        while horizontalCut and verticalCut:
            if horizontalCut[-1] >= verticalCut[-1]:
                res += horizontalCut.pop(-1) * num_vert
                num_hori += 1
            else:
                res += verticalCut.pop(-1) * num_hori
                num_vert += 1
        while horizontalCut:
            res += horizontalCut.pop(-1) * num_vert
        while verticalCut:
            res += verticalCut.pop(-1) * num_hori
        return res
