class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hcnt  = 1
        vcnt  = 1
        arr = []
        for c in horizontalCut:
            arr.append([c,0])
        for c in verticalCut:
            arr.append([c,1])
        arr.sort(reverse = True)
        ans = 0
        # print(arr)
        for c,t in arr:
            if t ==0:
                ans += (c*hcnt)
                vcnt +=1
            else:
                ans += (c*vcnt)
                hcnt +=1
            # print(ans,c,vcnt,hcnt)
        return ans
