class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        total = sum(horizontalCut) * n + sum(verticalCut) * m
        horizontalCut = sorted(horizontalCut)
        verticalCut = sorted(verticalCut)
        for x in horizontalCut:
            l = 0
            h = n - 1
            while l < h:
                mid = int((l + h) >> 1)
                if x >= verticalCut[mid]:
                    l = mid + 1
                else:
                    h = mid
            total -= x * l
        for x in verticalCut:
            l = 0
            h = m - 1
            while l < h:
                mid = int((l + h) >> 1)
                if x > horizontalCut[mid]:
                    l = mid + 1
                else:
                    h = mid
            total -= l * x
        return total
