class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Integer[] hCuts = Arrays.stream(horizontalCut).boxed().toArray(Integer[]::new);
        Integer[] vCuts = Arrays.stream(verticalCut).boxed().toArray(Integer[]::new);
        Arrays.sort(hCuts, Collections.reverseOrder());
        Arrays.sort(vCuts, Collections.reverseOrder());
        int hIndex = 0, vIndex = 0;
        long hParts = 1, vParts = 1;
        long totalCost = 0;
        while (hIndex < hCuts.length && vIndex < vCuts.length) {
            if (hCuts[hIndex] >= vCuts[vIndex]) {
                totalCost += hCuts[hIndex] * vParts;
                hParts++;
                hIndex++;
            } else {
                totalCost += vCuts[vIndex] * hParts;
                vParts++;
                vIndex++;
            }
        }
        while (hIndex < hCuts.length) {
            totalCost += hCuts[hIndex] * vParts;
            hParts++;
            hIndex++;
        }
        while (vIndex < vCuts.length) {
            totalCost += vCuts[vIndex] * hParts;
            vParts++;
            vIndex++;
        }
        return totalCost;
    }
    public static void main(String[] args) {
        Solution solution = new Solution();
        int[] horizontalCut1 = {1, 3};
        int[] verticalCut1 = {5};
        long result1 = solution.minimumCost(3, 2, horizontalCut1, verticalCut1);
        System.out.println(result1);
        int[] horizontalCut2 = {7};
        int[] verticalCut2 = {4};
        long result2 = solution.minimumCost(2, 2, horizontalCut2, verticalCut2);
        System.out.println(result2);
        int[] horizontalCut3 = {2, 1, 3};
        int[] verticalCut3 = {5, 4};
        long result3 = solution.minimumCost(4, 3, horizontalCut3, verticalCut3);
        System.out.println(result3);
    }
}
