class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h,v = 1,1
        cut = list(map(lambda e:(e,'h'), horizontalCut))+list(map(lambda e:(e,'v'), verticalCut))
        cut.sort(reverse=True)

        result = 0
        for c in cut:
            if c[1]=='h':
                result += c[0]*v
                h += 1
            else:
                result += c[0]*h
                v += 1
        return result
