class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalPieces=1
        verticalPieces=1
        cutsToPerform=[]
        for cut in horizontalCut:
            cutsToPerform.append((cut, 'H'))
        for cut in verticalCut:
            cutsToPerform.append((cut, 'V'))
        cutsToPerform.sort(reverse=True)
        cost=0
        for cutPair in cutsToPerform:
            cut, direction = cutPair
            if direction=='H':
                cost+=verticalPieces*cut
                horizontalPieces+=1
            else:
                cost+=horizontalPieces*cut
                verticalPieces+=1
        return cost
