class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& hori, vector<int>& verti) {
            sort(hori.begin(),hori.end(),greater<int>());
        sort(verti.begin(),verti.end(),greater<int>());


   ll h=1,v=1;
        int i=0,j=0;ll ans=0;
        while(i<hori.size() && j<verti.size()){
             if(hori[i]>=verti[j]){
                 ans+=(ll)((ll)hori[i]*(ll)v);
                 h++;
                 i++;
             }
            else{
                ans+=(ll)((ll)verti[j]*(ll)h);
                v++;
                j++;
            }
        }

        while(i<hori.size()){
             ans+=(ll)((ll)hori[i]*(ll)v);
                 h++;
                 i++;
        }


        while(j<verti.size()){
            ans+=(ll)((ll)verti[j]*(ll)h);
                v++;
                j++;
        }


        return (ll)ans;
    }
};
