class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:

        q = []
        for num in horizontalCut:
            heapq.heappush(q, (-num, 'h'))
        for num in verticalCut:
            heapq.heappush(q, (-num, 'v'))

        h, v = 1, 1
        res = 0

        while q:
            num, ch = heapq.heappop(q)
            if ch == 'v':
                res += -num * v
                h += 1
            else:
                res += -num * h
                v += 1
        return res
