class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue <pair <int , int>, vector<pair<int , int>>> pq;
        for (int i : hc) pq.push({i , 0});
        for (int i : vc) pq.push({i , 1});
        long long int  ans = 0;
        int x = 1; int y = 1;
        while (!pq.empty()) {
            int a = pq.top().first;
            int b = pq.top().second;
            pq.pop();
            if (b == 0) {
                ans += a * y;
                x++;
            }
            else {
                ans += a * x;
                y++;
            }
        }
        return ans;
    }
};
