class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        v = 0
        h = 0
        arr = [(horizontalCut[i], "h") for i in range(len(horizontalCut))] + [(verticalCut[i], "v") for i in range(len(verticalCut))]
        arr.sort(reverse=True)
        ans = 0
        for cost, type_ in arr:
            if type_ == "h":
                ans += (v+1)*cost
                h+=1
            else:
                ans += (h+1)*cost
                v+=1
        return ans
