class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        using int64 = long long;
        using ii = pair<int, int>;
        vector<ii> a;
        for (auto& x : h) a.push_back({x, 0});
        for (auto& x : v) a.push_back({x, 1});
        sort(a.rbegin(), a.rend());
        int64 ret = 0, cntH = 1, cntV = 1;
        for (auto [x, k] : a) {
            if (k == 0) {
                ret += cntV * x;
                cntH += 1;
            } else {
                ret += cntH * x;
                cntV += 1;
            }
        }
        return ret;
    }
};
