class Solution {
public:
    static bool cmp(int &a,int &b){
        return a>b;
    }
    long long minimumCost(int m, int n, vector<int>& hz, vector<int>& vc) {
        sort(hz.begin(),hz.end(),cmp);
        sort(vc.begin(),vc.end(),cmp);
        long long h=0,v=0;
        long long hp=0,vp=0;
        long long cost=0;
        while(hp<m-1 && vp<n-1){
            if(hz[hp]>vc[vp]){
                long long t=hz[hp];
                hp++;
                cost=cost+(v+1)*t;
                h++;
            }
            else{
                long long t=vc[vp];
                vp++;
                cost=cost+(h+1)*t;
                v++;
            }
        }
        while(hp<m-1){
            long long t=hz[hp];
            hp++;
            cost=cost+(v+1)*t;
            h++;
        }
        while(vp<n-1){
            long long t=vc[vp];
            vp++;
            cost=cost+(h+1)*t;
            v++;
        }
        return cost;
    }
};
