class Solution {
public:
  long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
    vector<pair<int, int>> costs;
    for (int i = 0; i < m - 1; i++)
      costs.push_back({horizontalCut[i], i});
    for (int i = 0; i < n - 1; i++)
      costs.push_back({verticalCut[i], m + i});
    sort(rbegin(costs), rend(costs));
    long long ans = 0;
    int hor = 1, ver = 1;
    for (auto [c, id] : costs)
      if (id < m)
      {
        ans += 1LL * ver * c;
        hor++;
      }
      else
      {
        ans += 1LL * hor * c;
        ver++;
      }
    return ans;
  }
};
