class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h = sorted(horizontalCut, reverse=True)
        v = sorted(verticalCut, reverse=True)

        hc, vc = 1, 1
        cost = 0
        i, j = 0, 0

        while i < len(h) and j < len(v):
            if h[i] >= v[j]:
                cost += h[i] * vc
                hc += 1
                i += 1
            else:
                cost += v[j] * hc
                vc += 1
                j += 1

        while i < len(h):
            cost += h[i] * vc
            i += 1

        while j < len(v):
            cost += v[j] * hc
            j += 1

        return cost
