class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         priority_queue<pair<int,int>> h;

        for(int x : horizontalCut) {
            h.push({x,1});

        }

        for(int x : verticalCut) {
            h.push({x,0});

        }

        int a = 0;
        int b = 0;

        long long res = 0;

        while(h.size()) {
            auto t = h.top();
            h.pop();


            if(t.second) {
               a ++ ; 
               res += t.first * (b + 1);

            } 
            else {
                b ++ ;

                res += t.first * (a + 1);

            }
        }


        return res;
    }
};
