class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        verticals = 0
        horizontals = 0
        totalCost = 0
        cuts = []
        for cut in horizontalCut:
            cuts.append((cut, "h"))
        for cut in verticalCut:
            cuts.append((cut, "v"))
        cuts.sort(reverse=True)
        for cost, direction in cuts:
            if direction == "h":
                totalCost += cost * (verticals + 1)
                horizontals += 1
            else:
                totalCost += cost * (horizontals + 1)
                verticals += 1

        return totalCost
