class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        # 先按照切割成本降序排序
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)

        res = 0  # 初始化总成本为0
        hCutsLeft, vCutsLeft = m - 1, n - 1  # 还可以进行的水平和垂直切割次数

        # 当还有切割可进行时
        while hCutsLeft > 0 or vCutsLeft > 0:
            # 若还可以进行水平切割
            if hCutsLeft > 0:
                nextHCost = horizontalCut[m - 1 - hCutsLeft]  # 下一次水平切割的成本
            else:
                nextHCost = float('-inf')  # 不再进行水平切割

            # 若还可以进行垂直切割
            if vCutsLeft > 0:
                nextVCost = verticalCut[n - 1 - vCutsLeft]  # 下一次垂直切割的成本
            else:
                nextVCost = float('-inf')  # 不再进行垂直切割

            # 比较并执行成本较高的切割
            if nextHCost > nextVCost:
                # 水平切割：切割成本乘以当前垂直分割块数
                res += nextHCost * (n - vCutsLeft)
                hCutsLeft -= 1  # 减少剩余的水平切割次数
            else:
                # 垂直切割：切割成本乘以当前水平分割块数
                res += nextVCost * (m - hCutsLeft)
                vCutsLeft -= 1  # 减少剩余的垂直切割次数

        return res
