class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pair<int, char>>cuts;
        for (auto i: hc) {
            cuts.push_back({i,'H'});
        }
        for (auto i: vc) {
            cuts.push_back({i,'V'});
        }
        sort(cuts.begin(), cuts.end(), greater<pair<int,char>>());

        long long h=1, v=1;
        long long total=0;
        for (auto i : cuts) {
            int cost = i.first;
            char type = i.second;
            if (type == 'H') {
                total += 1LL*cost*v;
                h++;
            }else{
                total += 1LL*cost*h;
                v++;
            }
        }
        return total;
    }
};
