class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        n--,m--;
        long long i = m-1, j = n-1, ans = 0;
        while(i >= 0 && j >= 0){
            if(h[i] < v[j]){
                ans += v[j--]*(m-i);
            }
            else ans += h[i--]*(n-j);
        }
        while(i >= 0){
            ans += h[i--]*(n-j);
        }
        while( j >= 0)ans += v[j--]*(m-i);
        return ans;
    }
};
