class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());

        long long int i=0;
        long long int j=0;

        long long int cnt1=1;
        long long int cnt2=1;

        long long int ans=0;

        while(i<m-1 && j<n-1){
            if(h[i]>v[j]){
                cnt2++;
                ans+=(long long)(h[i]*(long long)cnt1);
                i++;
            }else{
                cnt1++;
                ans+=(long long)((long long)v[j]*(long long)cnt2);
                j++;
            }
        }

        while(i<m-1){
            cnt2++;
                ans+=(long long)((long long)h[i]*(long long)cnt1);
                i++;
        }

        while(j<n-1){
            cnt1++;
                ans+=(long long)((long long)v[j]*(long long)cnt2);
                j++;
        }

        return (long long)ans;
    }
};
