class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hCost = list(sorted(horizontalCut))
        vCost = list(sorted(verticalCut))
        hMultipler = 1
        vMultipler = 1
        result = 0
        while hCost or vCost:
            if not hCost:
                result += sum(vCost) * vMultipler
                return result
            if not vCost:
                result += sum(hCost) * hMultipler
                return result
            if hCost[-1] > vCost[-1]:
                result += hCost[-1] * hMultipler
                hCost.pop()
                vMultipler += 1
            else:
                result += vCost[-1] * vMultipler
                vCost.pop()
                hMultipler += 1
        return result
