class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h = []

        for x in horizontalCut:
            heappush(h,[-x,1])
        for x in verticalCut:
            heappush(h,[-x,2])

        nofv =1
        nofh =1
        ans=0 
        while h:
            curr= heappop(h)
            if curr[1] == 1:
                ans+=(-curr[0]*nofv)
                nofh+=1
            else:
                ans+=(-curr[0]*nofh)
                nofv+=1
        return ans
